import cause1 from './images/afghanistan.jpg'
import React, { useEffect, useState } from 'react';
import { ethers } from 'ethers';
import {FUNDRAISER_ADDRESS, FUNDRAISER_ABI} from './config.js'

import { Link, Switch, Route, BrowserRouter as Router} from 'react-router-dom';

const Cause1= ({props}) => {
    const [cause1Title, setCause1Title] = useState();
    const [targetAmount, setTargetAmount] = useState();
    const [donatedAmount, setDonatedAmount] = useState();

    useEffect(() => {
    async function load() {
      const provider = new ethers.providers.JsonRpcProvider("http://localhost:7545")

      const contract = new ethers.Contract(
        FUNDRAISER_ADDRESS, FUNDRAISER_ABI, provider
      )

      const causeNames = await contract.getCauseNames();
      setCause1Title(causeNames[0]);

      const target = await contract.getCauseTargetAmount(cause1Title);
      const donated = await contract.getCauseTargetAmount(cause1Title);

      setTargetAmount(target);
      setDonatedAmount(donated);
    }

    load();
   }, []);


return (

    <div className="donate">
        <header className='header-cause'>

            <img src={cause1} className='img-cause'/>
                {cause1Title}
            <p className='p-cause'>
                With the freezeing cold winter and collapsing economy, people of Afghanistan barely have anything to eat.
            </p>
            <p className='p-cause'>
                Let us all come together to help them.
            </p>

            <p className='space'>
                Empty space
            </p>

            <p className='target-amount'>
                Target Amount = {targetAmount} ETH
            </p>

            <p className='current-amount'>
                Current Donations = {donatedAmount} ETH
            </p>

            <div className="form">
                <div className="form-group">
                    <label htmlFor="amount">Amount</label>
                    <input type="amount" name="amount" placeholder="amount" />
                </div>
            </div>

              <button type="button" className="donate-btn">
                <Link to="/popup">
                    Donate
                </Link>
              </button>
        </header>
    </div>
);
}


export default Cause1;
