import cause2 from './images/palestine.jpg'

import { Link, Switch, Route, BrowserRouter as Router} from 'react-router-dom';

const Cause2= ({props}) => {
  const [cause1Title, setCause1Title] = useState();
  const [cause1Desc, setCause1Desc] = useState();
  const [targetAmount, setTargetAmount] = useState();
  const [donatedAmount, setDonatedAmount] = useState();

  useEffect(() => {
  async function load() {
    const provider = new ethers.providers.JsonRpcProvider("http://localhost:7545")

    const contract = new ethers.Contract(
      FUNDRAISER_ADDRESS, FUNDRAISER_ABI, provider
    )

    const causeNames = await contract.getCauseNames();
    setCause1Title(causeNames[0]);

    const desc1 = String(await contract.getCauseDesc(cause1Title));
    setCause1Desc(desc1);

    const target = await contract.getCauseTargetAmount(cause1Title);
    const donated = await contract.getCauseDonatedAmount(cause1Title);

    setTargetAmount(String(target));
    setDonatedAmount(String(donated));
  }

  load();
 }, []);


return (

    <div className="donate">
        <header className='header-cause'>
            <img src={cause2} className='img-cause'/>
                Save Palestine
            <p className='p-cause'>
                Due to the ongoing war, people have lost their homes, jobs, and their loved ones. Lets all come together and build Palestine back.
            </p>
            <p className='space'>
                Empty space
            </p>

            <p className='target-amount'>
                Target Amount = {target_amount_str}
            </p>

            <p className='current-amount'>
                Current Donations = {current_donations_str}
            </p>
            <div className="form">
                <div className="form-group">
                    <label htmlFor="amount">Amount</label>
                    <input type="amount" name="amount" placeholder="amount" />
                </div>
            </div>

              <button type="button" className="donate-btn">
                <Link to="/popup">
                    Donate
                </Link>
              </button>
        </header>
    </div>
);
}


export default Cause2;
