import cause1 from './images/afghanistan.jpg'
import cause2 from './images/palestine.jpg'
import cause3 from './images/hxn.jpg'
import logo from './images/logo.svg'


import { ethers } from 'ethers';
import {FUNDRAISER_ADDRESS, FUNDRAISER_ABI} from './config.js'
import { Link, Switch, Route, BrowserRouter as Router} from 'react-router-dom';

const AdminPortal= ({props}) => {
return (
    <div className='dashboard' style={{marginTop: 150}}>
            <Admin/>
            <CausesList/>
            <Logo/>
    </div>
);
}

function Logo(){
    return(
        <div className='App'>
            <img src={logo} className='admin-logo' alt='logo' />
        </div>
    );

}

function Admin(){


    var contract_balance = 1000;
    var contract_balance_str = '$' + contract_balance;
    return(
        <div className="portal">
            Welcome To Admin Dashboard!

            <p className='space'>
                Empty space
            </p>

            <p className='portal-balance'>
                General Funds = {contract_balance_str}
            </p>
        </div>
    );

}
function CausesList(){
    const afg = "With the freezeing cold winter and collapsing economy, people of Afghanistan barely have anything to eat. Let us all come together to help them.";
    const pal = "Due to the ongoing war, people have lost their homes, jobs, and their loved ones. They need our help right now desperately. Lets all come together and build Palestine back.";
    const hxn = "He might seem a nice guy but in reality he is a mental patient. As he is just a student and thinks he is not mentally ill, he doesn't get treatment. Lets all contribute to get him treated (or get rid of him xD)";
    return (
        <div className="Causes-wrapper">
            <Cause img={cause1} title="Save Afghanistan" desc = {afg} add = "/admincause1" add1 = '/popup'/>
            <Cause img={cause2} title="Save Palestine" desc = {pal} add = "/admincause2" add1 = '/popup'/>
            <Cause img={cause3} title="Save Hxn" desc = {hxn} add = "/admincause3" add1 = '/popup'/>
        </div>
    );
}

function Cause(props){
    return(
        <div className="Cause">
            <div className="Cause-body">
                <img src={props.img} className='Cause-img'/>
                <h2 className="cause-title">
                   {props.title}
                </h2>
                <p className="cause-des">
                    {props.desc}
                </p>
            </div>
            <button className='cause-btn'>
                <Link to={props.add}>
                    Allocate Funds
                </Link>
            </button>

            <button className='cause-btn'>
                <Link to={props.add1}>
                    Confirm Payment
                </Link>
            </button>
        </div>
    )
}

export default AdminPortal;
