import cause1 from './images/afghanistan.jpg'
import React, { useEffect, useState } from 'react';
import { ethers } from 'ethers';
import {FUNDRAISER_ADDRESS, FUNDRAISER_ABI} from './config.js'

import { Link, Switch, Route, BrowserRouter as Router} from 'react-router-dom';



const Cause1= ({props}) => {
    const [targetAmount, setTargetAmount] = useState();
    const [donatedAmount, setDonatedAmount] = useState();

    var target_amount = 25000000;
    var target_amount_str = '$' + target_amount;

    var current_donations = 1000;
    var current_donations_str = '$' + current_donations;

    useEffect(() => {
    async function load() {
      const provider = new ethers.providers.JsonRpcProvider("http://localhost:7545")
      const accounts = await provider.listAccounts()

      const contract = new ethers.Contract(
        FUNDRAISER_ADDRESS, FUNDRAISER_ABI, provider
      )

      const causeNames = await contract.getCauseNames();
      setCause1Title(causeNames[0]);
      setCause2Title(causeNames[1]);
      setCause3Title(causeNames[2]);

      const desc1 = String(await contract.getCauseDesc(cause1Title));
      const desc2 = String(await contract.getCauseDesc(cause2Title));
      const desc3 = String(await contract.getCauseDesc(cause3Title));

      setCause1Desc(desc1);
      setCause2Desc(desc2);
      setCause3Desc(desc3);

      const status1 = await contract.getCauseDonationAcceptance(cause1Title);
      const status2 = await contract.getCauseDonationAcceptance(cause2Title);
      const status3 = await contract.getCauseDonationAcceptance(cause3Title);

      setCause1Status(status1);
      setCause2Status(status2);
      setCause3Status(status3);
    }

    load();
   }, []);


return (

    <div className="donate">
        <header className='header-cause'>

            <img src={cause1} className='img-cause'/>
                Save Afghanistan
            <p className='p-cause'>
                With the freezeing cold winter and collapsing economy, people of Afghanistan barely have anything to eat.
            </p>
            <p className='p-cause'>
                Let us all come together to help them.
            </p>

            <p className='space'>
                Empty space
            </p>

            <p className='target-amount'>
                Target Amount = {target_amount_str} ETH
            </p>

            <p className='current-amount'>
                Current Donations = {current_donations_str} ETH
            </p>

            <div className="form">
                <div className="form-group">
                    <label htmlFor="amount">Amount</label>
                    <input type="amount" name="amount" placeholder="amount" />
                </div>
            </div>

              <button type="button" className="donate-btn">
                <Link to="/popup">
                    Donate
                </Link>
              </button>
        </header>
    </div>
);
}


export default Cause1;
